select * from hosvital.hojobl where hojnumobl = '490296' and cntvig=2019;  -- 13020501

select  * from hosvital.admglo11 where mpnfac = 490296;
select  * from hosvital.admglo01 where aglremnr  = 19697;


select hojnumobl,cntvig,cntcod, hojfchrad from hosvital.hojobl where hojnumobl = '545706' and cntvig=2019;  -- 13020501

select  * from hosvital.admglo11 where mpnfac = 545706;
select  * from hosvital.admglo01 where aglremnr  = 21806;

update hosvital.hojobl h
set h.hojfchrad = (select  concat(concat(concat(concat(concat(year(adm01.aglradfc),'-') , lpad(month(adm01.aglradfc),'2',0)) ,'-'), lpad(day(adm01.aglradfc),'2',0)) ,' 00:00:01')
	 from hosvital.admglo01 adm01, hosvital.admglo11 adm11
	where adm01.aglremnr = adm11.aglremnr and adm11.mpnfac = h.hojnumobl)
where h.hojnumobl= '490296' and h.empcod='01'and h.cntvig=2019  -- and h.cntcod= '13020501'
 and h.hojfchrad='0001-01-01 00:00:00.0000';


select adm01.aglremnr, 
concat(concat(year(adm01.aglradfc),'-') , lpad(month(adm01.aglradfc),'2',0)) ,
concat(concat(concat(concat(concat(year(adm01.aglradfc),'-') , lpad(month(adm01.aglradfc),'2',0)) ,'-'), lpad(day(adm01.aglradfc),'2',0)) ,' 00:00:01')
from hosvital.admglo01 adm01
where adm01.aglremnr =19697;

