cntvig=(t1.cntvig) and x.cntcod = t1.cntcod and cast( (case when x.hoptppz='' then 0 else x.hoptppz end)  as numeric) = cast( (case when t1.hoptppz='' then 0 else t1.hoptppz end) as numeric) and year(date(x.hopfchobl)) = (t1.cntvig) and month(date(x.hopfchobl))=9) as Sep_2019,(select sum(hoptotcre- hoptotdeb) from hosvital.hojoblprv x where x.empcod = t1.empcod and x.prvcod= t1.prvcod and x.cntvig= (t1.cntvig)  and x.cntcod = t1.cntcod and cast( (case when x.hoptppz='' then 0 else x.hoptppz end)  as numeric) = cast( (case when t1.hoptppz='' then 0 else t1.hoptppz end) as numeric) and year(date(x.hopfchobl)) = (t1.cntvig) and month(date(x.hopfchobl)) =10 ) as Oct_2019,(select sum (hoptotcre-hoptotdeb) from hosvital.hojoblprv x where x.empcod = t1.empcod and x.prvcod= t1.prvcod and x.cntvig= (t1.cntvig) and x.cntcod = t1.cntcod and cast( (case when x.hoptppz='' then 0 else x.hoptppz end) as numeric)=cast( (case when t1.hoptppz='' then 0 else t1.hoptppz end) as numeric) and year(date(x.hopfchobl)) = (t1.cntvig) and month (date(x.hopfchobl))=11) as Nov_2019, (select sum(hoptotcre-hoptotdeb) from hosvital.hojoblprv x where x.empcod = t1.empcod and x.prvcod= t1.prvcod and x.cntvig= (t1.cntvig) and x.cntcod = t1.cntcod and cast( (case when x.hoptppz='' then 0 else x.hoptppz end)  as numeric)= cast((case when t1.hoptppz='' then 0 else t1.hoptppz end) as numeric) and year(date(x.hopfchobl)) =(t1.cntvig) and month(date(x.hopfchobl)) =12) as Dic_2019,(select sum(hoptotcre-hoptotdeb) from hosvital.hojoblprv x where x.empcod = t1.empcod and x.prvcod= t1.prvcod and x.cntvig=t1.cntvig and x.cntcod = t1.cntcod and cast( (case when x.hoptppz='' then 0 else x.hoptppz end) as numeric)=cast((case when t1.hoptppz='' then 0 else t1.hoptppz end) as numeric) and year(date(x.hopfchobl))>= (t1.cntvig-2)) as Total from hosvital.hojoblprv t1 inner join hosvital.terceros t2 on (t2.trccod=t1.prvcod) where t1.cntvig='2019' and  t1.mcdpto='001' GROUP BY t1.empcod,T1.CNTVIG,T1.CNTCOD,prvcod,trcrazs